import React from 'react';
import { ChatBubbleBottomCenterTextIcon } from '@heroicons/react/24/solid';

interface TypingIndicatorProps {
  className?: string;
}

const TypingIndicator: React.FC<TypingIndicatorProps> = ({ className }) => {
  return (
    <div className={`flex ml-14 mb-6 animate-fadeIn ${className || ''}`}>
      <div className="flex items-center gap-3">
        <div className="flex-shrink-0">
          <div className="w-10 h-10 rounded-full bg-gradient-to-br from-primary-400 to-primary-600 flex items-center justify-center shadow-md">
            <ChatBubbleBottomCenterTextIcon className="h-5 w-5 text-white" />
          </div>
        </div>
        
        <div className="bg-white dark:bg-dark-800 border border-gray-100 dark:border-dark-700 rounded-2xl px-5 py-4 shadow-lg">
          <div className="flex items-center gap-3">
            <div className="flex gap-1">
              <div className="w-2.5 h-2.5 bg-primary-400 dark:bg-primary-500 rounded-full animate-pulse" style={{ animationDuration: '0.8s', animationDelay: '0ms' }}></div>
              <div className="w-2.5 h-2.5 bg-primary-500 dark:bg-primary-400 rounded-full animate-pulse" style={{ animationDuration: '0.8s', animationDelay: '150ms' }}></div>
              <div className="w-2.5 h-2.5 bg-primary-600 dark:bg-primary-300 rounded-full animate-pulse" style={{ animationDuration: '0.8s', animationDelay: '300ms' }}></div>
            </div>
            <span className="text-sm font-medium text-gray-600 dark:text-gray-400">Gemini is thinking...</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TypingIndicator;
