import React, { createContext, useState, useEffect, ReactNode, useContext } from 'react';
import { validateGeminiKey } from '../services/geminiService';

interface GeminiContextType {
  apiKey: string;
  isValidKey: boolean;
  isLoading: boolean;
  error: string | null;
  setApiKey: (key: string) => void;
  validateKey: () => Promise<boolean>;
  resetError: () => void;
}

const defaultValue: GeminiContextType = {
  apiKey: '',
  isValidKey: false,
  isLoading: false,
  error: null,
  setApiKey: () => {},
  validateKey: async () => false,
  resetError: () => {},
};

export const GeminiContext = createContext<GeminiContextType>(defaultValue);

interface GeminiProviderProps {
  children: ReactNode;
}

export const useGemini = () => {
  const context = useContext(GeminiContext);
  
  if (!context) {
    throw new Error('useGemini must be used within a GeminiProvider');
  }
  
  return context;
};

export const GeminiProvider: React.FC<GeminiProviderProps> = ({ children }) => {
  const [apiKey, setApiKey] = useState<string>(() => {
    // Try to get key from environment variables first, then localStorage
    const envKey = process.env.REACT_APP_GEMINI_API_KEY || '';
    const storedKey = localStorage.getItem('gemini_api_key') || '';
    return envKey || storedKey;
  });
  const [isValidKey, setIsValidKey] = useState<boolean>(false);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  // Validate API key when it changes
  useEffect(() => {
    const validateAPIKey = async () => {
      if (!apiKey) {
        setIsValidKey(false);
        return;
      }

      setIsLoading(true);
      setError(null);
      
      try {
        const validation = await validateGeminiKey(apiKey);
        setIsValidKey(validation.isValid);
        
        if (validation.isValid) {
          localStorage.setItem('gemini_api_key', apiKey);
        } else {
          setError(validation.errorMessage || 'Invalid API key. Please check and try again.');
        }
      } catch (err) {
        setError('Error validating API key. Please try again later.');
        setIsValidKey(false);
      } finally {
        setIsLoading(false);
      }
    };

    if (apiKey) {
      validateAPIKey();
    }
  }, [apiKey]);

  const validateKey = async (): Promise<boolean> => {
    if (!apiKey) {
      setError('Please enter an API key');
      return false;
    }

    setIsLoading(true);
    setError(null);
    
    try {
      const validation = await validateGeminiKey(apiKey);
      setIsValidKey(validation.isValid);
      
      if (validation.isValid) {
        localStorage.setItem('gemini_api_key', apiKey);
        return true;
      } else {
        setError(validation.errorMessage || 'Invalid API key. Please check and try again.');
        return false;
      }
    } catch (err) {
      setError('Error validating API key. Please try again later.');
      setIsValidKey(false);
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  const resetError = () => {
    setError(null);
  };

  return (
    <GeminiContext.Provider
      value={{
        apiKey,
        isValidKey,
        isLoading,
        error,
        setApiKey,
        validateKey,
        resetError,
      }}
    >
      {children}
    </GeminiContext.Provider>
  );
};
