import React, { useState } from 'react';
import { ClipboardDocumentIcon, CheckIcon, UserIcon } from '@heroicons/react/24/outline';
import { ChatBubbleBottomCenterTextIcon } from '@heroicons/react/24/solid';

interface MessageProps {
  content: string;
  timestamp: Date;
  isUser: boolean;
}

const Message: React.FC<MessageProps> = ({ content, timestamp, isUser }) => {
  const [copied, setCopied] = useState(false);
  
  const messageTime = new Date(timestamp).toLocaleTimeString([], {
    hour: '2-digit',
    minute: '2-digit'
  });

  // Format message content with Markdown-like syntax
  const formatContent = (text: string) => {
    // Process code blocks (```code```)
    text = text.replace(/```([\s\S]*?)```/g, '<pre class="bg-gray-100 dark:bg-dark-600 p-3 rounded-md overflow-x-auto text-sm font-mono my-2">$1</pre>');
    
    // Process inline code (`code`)
    text = text.replace(/`([^`]+)`/g, '<code class="bg-gray-100 dark:bg-dark-600 px-1 rounded font-mono text-sm">$1</code>');
    
    // Process bold (**text**)
    text = text.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');
    
    // Process italic (*text*)
    text = text.replace(/\*(.*?)\*/g, '<em>$1</em>');
    
    // Process URLs
    text = text.replace(
      /(https?:\/\/[^\s]+)/g, 
      '<a href="$1" target="_blank" rel="noopener noreferrer" class="text-primary-600 dark:text-primary-400 hover:underline">$1</a>'
    );
    
    // Process paragraphs
    text = text.split('\n\n').map(para => `<p>${para}</p>`).join('');
    
    // Process lists
    text = text.replace(/^- (.*)/gm, '<li>$1</li>');
    text = text.replace(/<li>.*?<\/li>/gs, match => `<ul class="list-disc list-inside my-2">${match}</ul>`);
    
    return text;
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(content);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className={`flex ${isUser ? 'justify-end' : 'justify-start'} mb-4 group`}>
      {!isUser && (
        <div className="flex-shrink-0 mr-2">
          <div className="w-8 h-8 rounded-full bg-primary-100 dark:bg-primary-900/30 flex items-center justify-center">
            <ChatBubbleBottomCenterTextIcon className="h-4 w-4 text-primary-500 dark:text-primary-400" />
          </div>
        </div>
      )}
      
      <div
        className={`relative message ${
          isUser 
            ? 'message-user' 
            : 'message-bot'
        } shadow-message dark:shadow-message-dark rounded-2xl px-4 py-3 max-w-xs sm:max-w-sm md:max-w-md lg:max-w-lg`}
      >
        <div 
          className="text-sm prose prose-sm max-w-none dark:prose-invert"
          dangerouslySetInnerHTML={{ __html: formatContent(content) }}
        />
        
        <div className="flex items-center justify-between mt-2 text-xs text-gray-500 dark:text-gray-400">
          <span>{messageTime}</span>
          
          <button
            onClick={handleCopy}
            className="opacity-0 group-hover:opacity-100 transition-opacity p-1 hover:text-gray-700 dark:hover:text-gray-300"
            aria-label="Copy message"
          >
            {copied ? (
              <CheckIcon className="h-4 w-4 text-green-500" />
            ) : (
              <ClipboardDocumentIcon className="h-4 w-4" />
            )}
          </button>
        </div>
      </div>
      
      {isUser && (
        <div className="flex-shrink-0 ml-2">
          <div className="w-8 h-8 rounded-full bg-secondary-100 dark:bg-secondary-900/30 flex items-center justify-center">
            <UserIcon className="h-4 w-4 text-secondary-500 dark:text-secondary-400" />
          </div>
        </div>
      )}
    </div>
  );
};

export default Message;
