import React, { useState } from 'react';
import { useGemini } from '../contexts/GeminiContext';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { KeyIcon, CheckCircleIcon, XCircleIcon } from '@heroicons/react/24/outline';
import ErrorAlert from './ErrorAlert';
import LoadingSpinner from './LoadingSpinner';

const SettingsContainer = styled(motion.div)`
  max-width: 600px;
  margin: 20px auto;
  padding: 20px;
  background: white;
  border-radius: 15px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h2`
  margin-bottom: 20px;
  color: #333;
  font-size: 24px;
`;

const InputGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  color: #666;
  font-size: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
  font-size: 16px;
  transition: all 0.3s ease;
  
  &:focus {
    outline: none;
    border-color: #007bff;
    box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.1);
  }
`;

const Button = styled(motion.button)`
  padding: 12px 24px;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    background: #0056b3;
  }
  
  &:disabled {
    background: #ccc;
    cursor: not-allowed;
  }
`;

const Status = styled.div<{ isValid: boolean }>`
  margin-top: 10px;
  padding: 10px;
  border-radius: 8px;
  background: ${props => props.isValid ? 'rgba(40, 167, 69, 0.1)' : 'rgba(220, 53, 69, 0.1)'};
  color: ${props => props.isValid ? '#28a745' : '#dc3545'};
  font-size: 14px;
`;

const GeminiSettings: React.FC = () => {
  const { apiKey, setApiKey, isKeyValid, isLoading, error } = useGemini();
  const [localApiKey, setLocalApiKey] = useState(apiKey);
  const [showKey, setShowKey] = useState(false);

  const handleSave = () => {
    setApiKey(localApiKey);
  };

  // Mask API key for display
  const displayKey = (key: string) => {
    if (!key) return '';
    if (showKey) return key;
    // Only show first 3 and last 4 characters
    return key.length > 7 
      ? `${key.substring(0, 3)}...${key.substring(key.length - 4)}`
      : '•'.repeat(key.length); // For very short keys, just use dots
  };

  return (
    <SettingsContainer
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
    >
      <Title>Gemini API Settings</Title>
      <InputGroup>
        <Label>API Key</Label>
        <Input
          type={showKey ? "text" : "password"}
          value={localApiKey}
          onChange={(e) => setLocalApiKey(e.target.value)}
          placeholder="Enter your Gemini API key"
        />
        <div className="relative">
          <div className="absolute inset-y-0 right-0 flex items-center pr-3">
            <button
              type="button"
              onClick={() => setShowKey(!showKey)}
              className="text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200 px-2"
            >
              {showKey ? 'Hide' : 'Show'}
            </button>
          </div>
        </div>
        {(isLoading || isKeyValid || error) && (
          <Status isValid={isKeyValid}>
            {isLoading ? 'Validating...' : error || 'API key is valid'}
          </Status>
        )}
      </InputGroup>
      <Button
        onClick={handleSave}
        disabled={isLoading || localApiKey === apiKey}
        whileHover={{ scale: 1.02 }}
        whileTap={{ scale: 0.98 }}
      >
        Save Settings
      </Button>
    </SettingsContainer>
  );
};

export default GeminiSettings;
