import { useState, useEffect, useCallback, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { connectSocket, sendMessage as socketSendMessage, onReceiveMessage, offReceiveMessage, disconnectSocket } from '../services/socketService';
import { getChatHistory } from '../services/apiService';
import { generateChatResponse } from '../services/openaiService';
import { useOpenAI } from './useOpenAI';

interface Message {
  _id: string;
  content: string;
  isUser: boolean;
  timestamp: Date;
}

// Demo messages for initial chat history
const DEMO_MESSAGES: Message[] = [
  {
    _id: '1',
    content: 'Hello! How can I help you today?',
    isUser: false,
    timestamp: new Date(Date.now() - 3600000) // 1 hour ago
  }
];

// Bot responses for demo mode (used as fallback if OpenAI fails)
const BOT_RESPONSES = [
  "I'm here to help! What can I do for you?",
  "That's an interesting question. Let me think about that...",
  "I understand what you're asking. Here's what I know about that topic.",
  "Thanks for sharing that information with me.",
  "Let me search for that information for you...",
  "I found several resources that might help with your question.",
  "Could you provide more details about your request?",
  "I'm designed to assist with a variety of topics. What else would you like to know?",
  "That's a great point! I appreciate your insight.",
  "I'm continuously learning to better assist users like you.",
];

// More specific responses based on keywords (used as fallback if OpenAI fails)
const KEYWORD_RESPONSES: Record<string, string[]> = {
  "hello": [
    "Hello there! How can I assist you today?",
    "Hi! What can I help you with?",
    "Greetings! How may I be of service?",
  ],
  "help": [
    "I'd be happy to help! What do you need assistance with?",
    "I'm here to help! Could you let me know what you're looking for?",
    "I can help with various tasks. What specifically do you need help with?",
  ],
  "weather": [
    "The weather today is looking great! Clear skies and 75°F.",
    "Today's forecast shows partly cloudy conditions with a high of 72°F.",
    "It looks like there might be some rain later today. Current temperature is 68°F.",
  ],
  "time": [
    `The current time is ${new Date().toLocaleTimeString()}.`,
    `It's currently ${new Date().toLocaleTimeString()} in your local time zone.`,
    `According to my clock, it's ${new Date().toLocaleTimeString()}.`,
  ],
  "thanks": [
    "You're welcome! Is there anything else I can help with?",
    "My pleasure! Let me know if you need anything else.",
    "Happy to help! Any other questions?",
  ],
  "bye": [
    "Goodbye! Have a great day!",
    "Farewell! Feel free to return if you have more questions.",
    "Until next time! Take care!",
  ],
};

export const useChat = () => {
  const { token, user } = useContext(AuthContext);
  const { apiKey, isValidKey } = useOpenAI();
  const [messages, setMessages] = useState<Message[]>([]);
  const [loading, setLoading] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Initialize chat and load history
  useEffect(() => {
    if (!token) return;

    const initializeChat = async () => {
      try {
        setLoading(true);
        
        // In a real app, we would connect to the socket server
        // connectSocket(token);
        
        // Try to load history from API, fall back to demo data if unavailable
        try {
          const history = await getChatHistory(token);
          setMessages(history);
        } catch (err) {
          // Use demo messages as fallback
          setMessages(DEMO_MESSAGES);
        }
        
        setError(null);
      } catch (err) {
        console.error('Error initializing chat:', err);
        // Still set demo messages even if there's an error
        setMessages(DEMO_MESSAGES);
        setError(null); // Clear error since we have fallback
      } finally {
        setLoading(false);
      }
    };

    initializeChat();

    // Clean up on unmount
    return () => {
      offReceiveMessage();
      disconnectSocket();
    };
  }, [token]);

  // Generate a response using OpenAI or fallback to demo responses
  const generateResponse = async (userMessage: string, chatHistory: Message[]): Promise<string> => {
    // If we have a valid OpenAI API key, use it
    if (apiKey && isValidKey) {
      try {
        const historyForAPI = chatHistory.slice(-10); // Only use last 10 messages to save tokens
        return await generateChatResponse(historyForAPI, apiKey);
      } catch (error) {
        console.error('OpenAI API error:', error);
        // Fall back to demo responses if OpenAI fails
      }
    }
    
    // Fallback to demo mode
    // Check for keyword-based responses
    const lowercaseContent = userMessage.toLowerCase();
    const matchingKeyword = Object.keys(KEYWORD_RESPONSES).find(keyword => 
      lowercaseContent.includes(keyword)
    );
    
    if (matchingKeyword) {
      // Get a random response for the matching keyword
      const responses = KEYWORD_RESPONSES[matchingKeyword];
      return responses[Math.floor(Math.random() * responses.length)];
    } else {
      // If no keyword matches, use generic responses
      return BOT_RESPONSES[Math.floor(Math.random() * BOT_RESPONSES.length)];
    }
  };

  // Send message function with bot response 
  const sendChatMessage = useCallback(async (content: string) => {
    if (!content.trim()) return;

    // Create a user message
    const userMessage: Message = {
      _id: Date.now().toString(),
      content,
      isUser: true,
      timestamp: new Date(),
    };

    // Add to messages state
    setMessages(prevMessages => [...prevMessages, userMessage]);
    
    // Start typing indicator
    setIsTyping(true);
    setError(null);

    try {
      // Generate response
      const chatHistory = [...messages, userMessage];
      const responseContent = await generateResponse(content, chatHistory);
      
      // Create bot message with response
      const botMessage: Message = {
        _id: (Date.now() + 1).toString(),
        content: responseContent,
        isUser: false,
        timestamp: new Date(),
      };
      
      // Add bot message to state
      setMessages(prevMessages => [...prevMessages, botMessage]);
    } catch (err: any) {
      console.error('Error generating response:', err);
      setError(err.message || 'Failed to generate a response. Please try again.');
    } finally {
      setIsTyping(false);
    }
  }, [messages, apiKey, isValidKey]);

  // Add a welcome message if chat is empty
  useEffect(() => {
    if (!loading && messages.length === 0) {
      setMessages([
        {
          _id: 'welcome',
          content: `Hello${user ? ' ' + user.username : ''}! How can I assist you today?`,
          isUser: false,
          timestamp: new Date(),
        },
      ]);
    }
  }, [loading, messages.length, user]);

  return {
    messages,
    loading,
    isTyping,
    error,
    sendMessage: sendChatMessage,
  };
};
