import React, { useRef, useEffect, useState } from 'react';
import Header from '../components/Header';
import Message from '../components/Message';
import ChatInput from '../components/ChatInput';
import ErrorAlert from '../components/ErrorAlert';
import LoadingSpinner from '../components/LoadingSpinner';
import TypingIndicator from '../components/TypingIndicator';
import useChat from '../hooks/useChat';
import { SparklesIcon } from '@heroicons/react/24/outline';

const Chat: React.FC = () => {
  const { messages, isLoading, isTyping, error, sendMessage } = useChat();
  const [showError, setShowError] = useState<boolean>(!!error);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  // Show error when error state changes
  useEffect(() => {
    setShowError(!!error);
  }, [error]);

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-gray-50 to-gray-100 dark:from-dark-900 dark:to-dark-800 transition-colors duration-200">
      <Header />
      
      <div className="flex-1 overflow-hidden flex flex-col max-w-6xl w-full mx-auto px-4 md:px-6 py-6">
        <div className="bg-white dark:bg-dark-800 rounded-2xl shadow-xl overflow-hidden flex flex-col flex-1">
          {/* Chat header */}
          <div className="p-4 md:p-6 bg-gradient-to-r from-primary-500 to-primary-600 text-white flex items-center justify-between">
            <h1 className="text-xl font-bold flex items-center gap-2">
              <SparklesIcon className="h-6 w-6 text-white animate-pulse" />
              Google Gemini Chat Assistant
            </h1>
            
            <div className="flex items-center gap-3">
              <span className="text-xs px-3 py-1.5 rounded-full bg-green-500/20 text-green-100">
                Gemini Enabled
              </span>
            </div>
          </div>
          
          {/* Chat messages */}
          <div className="flex-1 overflow-y-auto p-4 md:p-6 bg-gray-50 dark:bg-dark-900/50 backdrop-blur-sm">
            {showError && error && (
              <ErrorAlert 
                message={error} 
                onDismiss={() => setShowError(false)} 
              />
            )}
            
            {messages.length === 0 ? (
              <div className="h-full flex flex-col items-center justify-center text-center p-6">
                <div className="max-w-md space-y-6 bg-white dark:bg-dark-800 rounded-2xl p-8 shadow-lg">
                  <div className="h-20 w-20 bg-gradient-to-br from-primary-400 to-primary-600 rounded-full flex items-center justify-center mx-auto mb-2">
                    <SparklesIcon className="h-10 w-10 text-white" />
                  </div>
                  <h2 className="text-2xl font-bold text-gray-800 dark:text-white">Welcome to Gemini Chat</h2>
                  <p className="text-gray-600 dark:text-gray-300">
                    This AI assistant is powered by Google's Gemini AI model. Ask anything and get intelligent responses instantly.
                  </p>
                  <div className="pt-4 border-t border-gray-200 dark:border-dark-700">
                    <p className="text-gray-500 dark:text-gray-400 text-sm">
                      Start a conversation by typing a message below.
                    </p>
                  </div>
                </div>
              </div>
            ) : (
              <>
                {isLoading && messages.length === 0 ? (
                  <div className="h-full flex flex-col items-center justify-center">
                    <LoadingSpinner />
                    <p className="mt-4 text-gray-500 dark:text-gray-400">Loading conversation...</p>
                  </div>
                ) : (
                  <div className="space-y-4 pb-2 max-w-4xl mx-auto">
                    {messages.map((message) => (
                      <Message
                        key={message.id}
                        content={message.content}
                        timestamp={message.timestamp}
                        isUser={message.isUser}
                      />
                    ))}
                    {isTyping && <TypingIndicator />}
                    <div ref={messagesEndRef} />
                  </div>
                )}
              </>
            )}
          </div>
          
          {/* Chat input */}
          <ChatInput onSendMessage={sendMessage} disabled={isLoading || isTyping} />
        </div>
      </div>
    </div>
  );
};

export default Chat;
