import React, { useState } from 'react';
import { ClipboardDocumentIcon, CheckIcon, UserIcon } from '@heroicons/react/24/outline';
import { ChatBubbleBottomCenterTextIcon } from '@heroicons/react/24/solid';

interface MessageProps {
  content: string;
  timestamp: Date;
  isUser: boolean;
}

const Message: React.FC<MessageProps> = ({ content, timestamp, isUser }) => {
  const [copied, setCopied] = useState(false);
  
  const messageTime = new Date(timestamp).toLocaleTimeString([], {
    hour: '2-digit',
    minute: '2-digit'
  });

  // Format message content with Markdown-like syntax
  const formatContent = (text: string) => {
    // Process code blocks (```code```)
    text = text.replace(/```([\s\S]*?)```/g, '<pre class="bg-gray-100 dark:bg-dark-600 p-3 rounded-md overflow-x-auto text-sm font-mono my-2 border border-gray-200 dark:border-dark-500">$1</pre>');
    
    // Process inline code (`code`)
    text = text.replace(/`([^`]+)`/g, '<code class="bg-gray-100 dark:bg-dark-600 px-1 rounded font-mono text-sm border-b border-gray-200 dark:border-dark-500">$1</code>');
    
    // Process bold (**text**)
    text = text.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');
    
    // Process italic (*text*)
    text = text.replace(/\*(.*?)\*/g, '<em>$1</em>');
    
    // Process URLs
    text = text.replace(
      /(https?:\/\/[^\s]+)/g, 
      '<a href="$1" target="_blank" rel="noopener noreferrer" class="text-primary-600 dark:text-primary-400 hover:underline">$1</a>'
    );
    
    // Process paragraphs
    text = text.split('\n\n').map(para => `<p class="mb-2">${para}</p>`).join('');
    
    // Process lists
    text = text.replace(/^- (.*)/gm, '<li class="mb-1">$1</li>');
    text = text.replace(/<li.*?<\/li>/gs, match => `<ul class="list-disc list-inside my-2 space-y-1">${match}</ul>`);
    
    return text;
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(content);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className={`flex ${isUser ? 'justify-end' : 'justify-start'} mb-6 group animate-fadeIn`}>
      {!isUser && (
        <div className="flex-shrink-0 mr-3">
          <div className="w-10 h-10 rounded-full bg-gradient-to-br from-primary-400 to-primary-600 flex items-center justify-center shadow-md">
            <ChatBubbleBottomCenterTextIcon className="h-5 w-5 text-white" />
          </div>
        </div>
      )}
      
      <div
        className={`relative rounded-2xl px-5 py-4 max-w-xs sm:max-w-sm md:max-w-md lg:max-w-lg ${
          isUser 
            ? 'bg-gradient-to-r from-secondary-500 to-secondary-600 text-white shadow-lg'
            : 'bg-white dark:bg-dark-800 border border-gray-100 dark:border-dark-700 shadow-lg dark:shadow-dark-900/20'
        }`}
      >
        <div 
          className={`prose prose-sm max-w-none ${isUser ? 'prose-invert' : 'dark:prose-invert'}`}
          dangerouslySetInnerHTML={{ __html: formatContent(content) }}
        />
        
        <div className="flex items-center justify-between mt-2 text-xs">
          <span className={`${isUser ? 'text-secondary-200' : 'text-gray-500 dark:text-gray-400'}`}>
            {messageTime}
          </span>
          
          <button
            onClick={handleCopy}
            className="opacity-0 group-hover:opacity-100 transition-opacity p-1.5 rounded-full bg-white/10 backdrop-blur-sm hover:bg-white/20"
            aria-label="Copy message"
          >
            {copied ? (
              <CheckIcon className="h-4 w-4 text-green-400" />
            ) : (
              <ClipboardDocumentIcon className="h-4 w-4 text-white/70" />
            )}
          </button>
        </div>
      </div>
      
      {isUser && (
        <div className="flex-shrink-0 ml-3">
          <div className="w-10 h-10 rounded-full bg-gradient-to-br from-secondary-400 to-secondary-600 flex items-center justify-center shadow-md">
            <UserIcon className="h-5 w-5 text-white" />
          </div>
        </div>
      )}
    </div>
  );
};

export default Message;
