import React, { useRef, useEffect, useState } from 'react';
import Header from '../components/Header';
import Message from '../components/Message';
import ChatInput from '../components/ChatInput';
import ErrorAlert from '../components/ErrorAlert';
import LoadingSpinner from '../components/LoadingSpinner';
import { useChat } from '../hooks/useChat';

const Chat: React.FC = () => {
  const { messages, loading, error, sendMessage } = useChat();
  const [showError, setShowError] = useState<boolean>(!!error);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Show error when error state changes
  useEffect(() => {
    setShowError(!!error);
  }, [error]);

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      <Header />
      <div className="flex-1 overflow-hidden flex flex-col max-w-4xl w-full mx-auto">
        <div className="p-4 bg-white shadow-sm">
          <h1 className="text-xl font-bold text-gray-800">Chat with AI Assistant</h1>
        </div>
        
        <div className="flex-1 overflow-y-auto p-6">
          {showError && error && (
            <ErrorAlert 
              message={error} 
              onDismiss={() => setShowError(false)} 
            />
          )}
          
          {loading ? (
            <LoadingSpinner size="md" className="h-32" />
          ) : (
            <>
              {messages.map((message) => (
                <Message
                  key={message._id}
                  content={message.content}
                  timestamp={message.timestamp}
                  isUser={message.isUser}
                />
              ))}
              <div ref={messagesEndRef} />
            </>
          )}
        </div>
        
        <div className="mt-auto">
          <ChatInput onSendMessage={sendMessage} />
        </div>
      </div>
    </div>
  );
};

export default Chat;
