import React, { useRef, useEffect, useState } from 'react';
import Header from '../components/Header';
import Message from '../components/Message';
import ChatInput from '../components/ChatInput';
import ErrorAlert from '../components/ErrorAlert';
import LoadingSpinner from '../components/LoadingSpinner';
import TypingIndicator from '../components/TypingIndicator';
import OpenAISettings from '../components/OpenAISettings';
import useChat from '../hooks/useChat';
import { useOpenAI } from '../hooks/useOpenAI';
import { KeyIcon, SparklesIcon } from '@heroicons/react/24/outline';

const Chat: React.FC = () => {
  const { messages, loading, isTyping, error, sendMessage } = useChat();
  const { apiKey, isValidKey } = useOpenAI();
  const [showError, setShowError] = useState<boolean>(!!error);
  const [showApiSettings, setShowApiSettings] = useState<boolean>(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  // Show error when error state changes
  useEffect(() => {
    setShowError(!!error);
  }, [error]);

  // Check if API key is set on first load
  useEffect(() => {
    if (!apiKey && !loading) {
      setShowApiSettings(true);
    }
  }, [apiKey, loading]);

  return (
    <div className="min-h-screen flex flex-col bg-gray-50 dark:bg-dark-800 transition-colors duration-200">
      <Header />
      <div className="flex-1 overflow-hidden flex flex-col max-w-5xl w-full mx-auto">
        <div className="p-4 bg-white shadow-sm dark:bg-dark-700 dark:text-white transition-colors duration-200">
          <div className="flex items-center justify-between">
            <h1 className="text-xl font-bold text-gray-800 dark:text-white flex items-center gap-2">
              <SparklesIcon className={`h-5 w-5 ${isValidKey ? 'text-green-500' : 'text-gray-400'}`} />
              AI Chat Assistant
            </h1>
            
            <div className="flex items-center gap-2">
              {apiKey && (
                <span className={`text-xs px-2 py-1 rounded-full ${isValidKey ? 'bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-300' : 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/30 dark:text-yellow-300'}`}>
                  {isValidKey ? 'GPT Enabled' : 'GPT Disabled'}
                </span>
              )}
              
              <button 
                onClick={() => setShowApiSettings(true)}
                className="p-1.5 text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-300 rounded-full hover:bg-gray-100 dark:hover:bg-dark-600"
                aria-label="OpenAI API Settings"
              >
                <KeyIcon className="h-5 w-5" />
              </button>
            </div>
          </div>
        </div>
        
        <div className="flex-1 overflow-y-auto p-6 bg-grid-pattern dark:bg-grid-pattern-dark bg-fixed">
          {showError && error && (
            <ErrorAlert 
              message={error} 
              onDismiss={() => setShowError(false)} 
            />
          )}
          
          {loading ? (
            <div className="flex flex-col items-center justify-center h-32">
              <LoadingSpinner size="md" />
              <p className="mt-4 text-gray-500 dark:text-gray-400">Loading chat history...</p>
            </div>
          ) : (
            <>
              {messages.length === 0 ? (
                <div className="h-full flex flex-col items-center justify-center text-center p-6">
                  <div className="w-16 h-16 bg-primary-100 dark:bg-primary-900/30 rounded-full flex items-center justify-center mb-4">
                    <SparklesIcon className="h-8 w-8 text-primary-500 dark:text-primary-400" />
                  </div>
                  <h2 className="text-xl font-bold text-gray-800 dark:text-white mb-2">Welcome to AI Chat</h2>
                  <p className="text-gray-600 dark:text-gray-300 max-w-md mb-6">
                    Get instant, intelligent responses. Ask questions, get creative content, or just have a conversation.
                  </p>
                  {!apiKey && (
                    <button
                      onClick={() => setShowApiSettings(true)}
                      className="btn btn-primary btn-dark gap-2"
                    >
                      <KeyIcon className="h-5 w-5" />
                      Connect OpenAI API
                    </button>
                  )}
                </div>
              ) : (
                <div className="space-y-4 pb-2">
                  {messages.map((message) => (
                    <Message
                      key={message._id}
                      content={message.content}
                      timestamp={message.timestamp}
                      isUser={message.isUser}
                    />
                  ))}
                  {isTyping && <TypingIndicator />}
                  <div ref={messagesEndRef} />
                </div>
              )}
            </>
          )}
        </div>
        
        <div className="mt-auto border-t border-gray-200 dark:border-dark-600">
          <ChatInput onSendMessage={sendMessage} disabled={loading || isTyping} />
        </div>
      </div>
      
      {showApiSettings && <OpenAISettings onClose={() => setShowApiSettings(false)} />}
    </div>
  );
};

export default Chat;
