import React, { useRef, useEffect, useState } from 'react';
import Message from '../components/Message';
import ChatInput from '../components/ChatInput';
import ErrorAlert from '../components/ErrorAlert';
import LoadingSpinner from '../components/LoadingSpinner';
import TypingIndicator from '../components/TypingIndicator';
import useChat from '../hooks/useChat';
import { SparklesIcon, PlusIcon, ArrowLeftIcon, FolderIcon, XMarkIcon, PencilIcon } from '@heroicons/react/24/outline';
import { ChatBubbleLeftRightIcon, DocumentTextIcon } from '@heroicons/react/24/solid';
import ThemeToggle from '../components/ThemeToggle';

const Chat: React.FC = () => {
  const { messages, isLoading, isTyping, error, sendMessage } = useChat();
  const [showError, setShowError] = useState<boolean>(!!error);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [activeChatName, setActiveChatName] = useState<string>("My Chat");
  const [activeTab, setActiveTab] = useState<string>("All");
  const [showSidebar, setShowSidebar] = useState<boolean>(true);
  const [newFolderName, setNewFolderName] = useState<string>("");
  const [isAddingFolder, setIsAddingFolder] = useState<boolean>(false);
  const [editingFolderId, setEditingFolderId] = useState<number | null>(null);
  const [editFolderName, setEditFolderName] = useState<string>("");

  // Folder management
  const [folders, setFolders] = useState([
    { id: 1, name: "Work chats", count: 7 },
    { id: 2, name: "Life chats", count: 5 },
    { id: 3, name: "Projects chats", count: 9 },
    { id: 4, name: "Clients chats", count: 4 }
  ]);

  const [recentChats, setRecentChats] = useState([
    { id: 1, name: "Plan a 3-day trip", snippet: "A 3-day trip to Paris with cultural highlights" },
    { id: 2, name: "Ideas for a customer loyalty program", snippet: "Create a tiered system for a customer loyalty..." },
    { id: 3, name: "Help me pick", snippet: "Help me pick gift options for my fishing-loving..." }
  ]);

  // Scroll to bottom when messages change or when typing animation is happening
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  // Show error when error state changes
  useEffect(() => {
    setShowError(!!error);
  }, [error]);

  // Handle adding a new folder
  const handleAddFolder = () => {
    if (newFolderName.trim()) {
      const newFolder = {
        id: Date.now(),
        name: newFolderName.trim(),
        count: 0
      };
      setFolders([...folders, newFolder]);
      setNewFolderName("");
      setIsAddingFolder(false);
    }
  };

  // Handle editing a folder
  const startEditingFolder = (folder: typeof folders[0]) => {
    setEditingFolderId(folder.id);
    setEditFolderName(folder.name);
  };

  const saveEditedFolder = () => {
    if (editFolderName.trim() && editingFolderId !== null) {
      setFolders(folders.map(folder => 
        folder.id === editingFolderId 
          ? { ...folder, name: editFolderName.trim() } 
          : folder
      ));
      setEditingFolderId(null);
    }
  };

  // Handle deleting a folder
  const deleteFolder = (id: number) => {
    setFolders(folders.filter(folder => folder.id !== id));
  };

  // Toggle sidebar for responsive design
  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  const tabs = ["All", "Text", "Image", "Video"];

  return (
    <div className="min-h-screen flex bg-black text-white">
      {/* Sidebar */}
      <div className="w-80 bg-zinc-900 border-r border-zinc-800 flex flex-col h-screen">
        <div className="p-4">
          <div className="flex items-center justify-between mb-4">
            <button className="flex items-center gap-2 bg-zinc-800 rounded-lg px-3 py-2 text-sm font-medium">
              <ChatBubbleLeftRightIcon className="h-5 w-5 text-white" />
              <span>My Chats</span>
            </button>
            <button className="p-1 rounded-lg hover:bg-zinc-800">
              <AdjustmentsHorizontalIcon className="h-5 w-5 text-zinc-400" />
            </button>
          </div>

          <div className="relative mb-4">
            <input 
              type="text" 
              placeholder="Search" 
              className="w-full bg-zinc-800 border-0 rounded-lg pl-9 pr-4 py-2 text-sm text-white placeholder-zinc-500 focus:ring-1 focus:ring-green-500 focus:outline-none"
            />
            <svg className="absolute left-3 top-2.5 h-4 w-4 text-zinc-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>

          {/* Folders */}
          <div className="mb-4">
            <div className="flex items-center justify-between mb-2">
              <h3 className="text-xs font-medium text-zinc-400">Folders</h3>
              <div className="flex space-x-1">
                <button className="p-1 rounded hover:bg-zinc-800">
                  <svg className="h-3.5 w-3.5 text-zinc-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                </button>
                <button className="p-1 rounded hover:bg-zinc-800">
                  <svg className="h-3.5 w-3.5 text-zinc-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                  </svg>
                </button>
              </div>
            </div>

            <div className="space-y-1">
              {folders.map((folder, index) => (
                <button key={index} className="flex items-center justify-between w-full px-3 py-2 rounded-lg hover:bg-zinc-800 text-left">
                  <div className="flex items-center">
                    <FolderIcon className="h-4 w-4 text-zinc-400 mr-2" />
                    <span className="text-sm text-zinc-300">{folder.name}</span>
                  </div>
                  <span className="text-xs text-zinc-500">{folder.count}</span>
                </button>
              ))}
            </div>
          </div>

          {/* Recent Chats */}
          <div>
            <div className="flex items-center justify-between mb-2">
              <h3 className="text-xs font-medium text-zinc-400">Chats</h3>
              <button className="p-1 rounded hover:bg-zinc-800">
                <svg className="h-3.5 w-3.5 text-zinc-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </button>
            </div>

            <div className="space-y-1">
              {recentChats.map((chat, index) => (
                <button key={index} className="flex flex-col w-full px-3 py-2 rounded-lg hover:bg-zinc-800 text-left">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-zinc-300">{chat.name}</span>
                    <span className="text-xs text-zinc-500">...</span>
                  </div>
                  <p className="text-xs text-zinc-500 truncate">{chat.snippet}</p>
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* New Chat Button */}
        <div className="mt-auto p-4">
          <button className="flex items-center justify-between w-full bg-zinc-800 hover:bg-zinc-700 rounded-lg px-4 py-2 text-sm font-medium transition-colors">
            <span className="flex items-center">
              <PlusIcon className="h-4 w-4 mr-2" />
              New chat
            </span>
            <span className="bg-zinc-700 p-1 rounded">
              <PlusIcon className="h-3 w-3" />
            </span>
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col h-screen overflow-hidden relative bg-zinc-900">
        {/* Chat Header */}
        <div className="flex items-center justify-between px-4 py-3 border-b border-zinc-800">
          <div className="flex items-center">
            <button className="mr-2 p-1.5 rounded-lg hover:bg-zinc-800">
              <ArrowLeftIcon className="h-5 w-5 text-zinc-400" />
            </button>
            <h2 className="text-sm font-medium flex items-center">
              {activeChatName}
              <span className="ml-2 text-xs px-2 py-0.5 bg-green-900 text-green-400 rounded-full">GPT-4.0</span>
            </h2>
          </div>

          <div className="flex items-center space-x-2">
            <button className="p-1.5 rounded-lg hover:bg-zinc-800">
              <BookmarkIcon className="h-5 w-5 text-zinc-400" />
            </button>
            <button className="p-1.5 rounded-lg hover:bg-zinc-800">
              <svg className="h-5 w-5 text-zinc-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 8V4m0 0h4M4 4l5 5m11-1V4m0 0h-4m4 0l-5 5M4 16v4m0 0h4m-4 0l5-5m11 5v-4m0 4h-4m4 0l-5-5" />
              </svg>
            </button>
            <ThemeToggle />
          </div>
        </div>

        {/* Chat Messages */}
        <div className="flex-1 overflow-y-auto py-4 px-4 md:px-8">
          {showError && error && (
            <ErrorAlert 
              message={error} 
              onDismiss={() => setShowError(false)} 
            />
          )}

          {messages.length === 0 ? (
            <div className="h-full flex flex-col items-center justify-center text-center">
              <div className="max-w-md">
                <div className="flex justify-center mb-6">
                  <SparklesIcon className="h-12 w-12 text-green-500" />
                </div>
                <h2 className="text-2xl font-bold mb-4">How can I help you today?</h2>
                <p className="text-sm text-zinc-400 mb-8">
                  This model can prompt editing the user for their name, and then it will display a greeting message with the name entered by the user.
                </p>

                <div className="grid grid-cols-3 gap-4 mb-8">
                  <div className="bg-zinc-800 p-4 rounded-lg">
                    <div className="flex justify-center mb-2">
                      <DocumentTextIcon className="h-6 w-6 text-green-500" />
                    </div>
                    <h3 className="text-sm font-medium mb-1">Smart Prompt Templates</h3>
                    <p className="text-xs text-zinc-500">Learn how to create prompts for better responses</p>
                  </div>

                  <div className="bg-zinc-800 p-4 rounded-lg">
                    <div className="flex justify-center mb-2">
                      <ChatBubbleLeftRightIcon className="h-6 w-6 text-green-500" />
                    </div>
                    <h3 className="text-sm font-medium mb-1">Model Type Selection</h3>
                    <p className="text-xs text-zinc-500">Choose different models for various tasks</p>
                  </div>

                  <div className="bg-zinc-800 p-4 rounded-lg">
                    <div className="flex justify-center mb-2">
                      <svg className="h-6 w-6 text-green-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5h12M9 3v2m1.048 9.5A18.022 18.022 0 016.412 9m6.088 9h7M11 21l5-10 5 10M12.751 5C11.783 10.77 8.07 15.61 3 18.129" />
                      </svg>
                    </div>
                    <h3 className="text-sm font-medium mb-1">Multilingual Support</h3>
                    <p className="text-xs text-zinc-500">Interact in multiple languages</p>
                  </div>
                </div>

                {/* Tabs */}
                <div className="flex justify-center space-x-4 mb-6">
                  {tabs.map(tab => (
                    <button
                      key={tab}
                      className={`text-xs px-2 py-1 rounded ${activeTab === tab ? 'text-green-500 border-b-2 border-green-500' : 'text-zinc-400'}`}
                      onClick={() => setActiveTab(tab)}
                    >
                      {tab}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          ) : (
            <div className="max-w-3xl mx-auto space-y-6">
              {messages.map((message) => (
                <Message
                  key={message.id}
                  content={message.content}
                  timestamp={message.timestamp}
                  isUser={message.isUser}
                />
              ))}
              {isTyping && <TypingIndicator />}
              <div ref={messagesEndRef} />
            </div>
          )}
        </div>

        {/* Input Area */}
        <div className="p-4 border-t border-zinc-800">
          <div className="max-w-3xl mx-auto relative">
            <div className="flex items-center bg-zinc-800 rounded-xl overflow-hidden">
              <button className="p-2 text-zinc-400 hover:text-zinc-300">
                <SparklesIcon className="h-5 w-5" />
              </button>

              <ChatInput 
                onSendMessage={sendMessage} 
                disabled={isLoading || isTyping} 
              />

              <button 
                className={`p-2 rounded-full mx-1 ${isLoading || isTyping ? 'bg-zinc-700 text-zinc-500' : 'bg-green-600 text-white hover:bg-green-500'}`}
                disabled={isLoading || isTyping}
              >
                <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 12h14M12 5l7 7-7 7" />
                </svg>
              </button>
            </div>

            <p className="text-xs text-center text-zinc-500 mt-2">
              ChatGPT can make mistakes. Consider checking important information.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
