import { GoogleGenerativeAI } from "@google/generative-ai";

// Hardcoded API key
const API_KEY = "AIzaSyAsdXIijrxubtnxPX6pSfXhwZnVc3N6HBw";

// Initialize the Gemini API client
const getGeminiClient = () => {
  return new GoogleGenerativeAI(API_KEY);
};

export const generateChatResponse = async (
  messages: { content: string; isUser: boolean }[]
): Promise<string> => {
  try {
    const genAI = getGeminiClient();
    const model = genAI.getGenerativeModel({ model: "gemini-1.0-pro" });

    // Start a new chat
    const chat = model.startChat();

    // Send each message in sequence to maintain context
    let response;
    for (const msg of messages) {
      response = await chat.sendMessage(msg.content);
    }

    if (!response) {
      throw new Error('No response received from Gemini API');
    }

    const text = await response.response.text();
    console.log('Received response from Gemini API:', text);
    return text;
  } catch (error: any) {
    console.error('Error calling Gemini API:', error);
    
    if (error.message) {
      throw new Error(`Gemini API Error: ${error.message}`);
    }
    
    throw new Error('Failed to generate response. Please try again later.');
  }
};

export const validateGeminiKey = async (): Promise<{isValid: boolean; errorMessage?: string}> => {
  try {
    const genAI = getGeminiClient();
    const model = genAI.getGenerativeModel({ model: "gemini-1.0-pro" });

    console.log('Validating Gemini API key...');
    const prompt = "Hello, this is a test message to validate the API key.";
    const result = await model.generateContent(prompt);
    const response = await result.response;
    const text = response.text();
    
    console.log('Key validation successful');
    return {isValid: true};
  } catch (error: any) {
    console.error('API key validation error:', error);
    
    let errorMessage = 'Invalid API key';
    if (error.message) {
      errorMessage = `Gemini API Error: ${error.message}`;
    }
    
    return {isValid: false, errorMessage};
  }
};
