import React from 'react';
import { ChatBubbleBottomCenterTextIcon } from '@heroicons/react/24/solid';
import { motion } from 'framer-motion';

interface TypingIndicatorProps {
  className?: string;
}

const TypingIndicator: React.FC<TypingIndicatorProps> = ({ className }) => {
  return (
    <div className={`flex mb-4 animate-fadeIn ${className || ''}`}>
      <div className="flex items-start gap-3">
        <div className="flex-shrink-0 mr-3">
          <div className="w-8 h-8 rounded-full bg-green-600 flex items-center justify-center">
            <ChatBubbleBottomCenterTextIcon className="h-4 w-4 text-white" />
          </div>
        </div>

        <motion.div 
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-zinc-800 rounded-lg px-4 py-3 shadow-lg max-w-xs sm:max-w-sm"
        >
          <div className="flex items-center gap-3">
            <div className="flex gap-1.5">
              <motion.div 
                className="w-2 h-2 bg-green-500 rounded-full" 
                animate={{ y: [0, -4, 0] }}
                transition={{ duration: 0.8, repeat: Infinity, ease: "easeInOut" }}
              />
              <motion.div 
                className="w-2 h-2 bg-green-500 rounded-full" 
                animate={{ y: [0, -4, 0] }}
                transition={{ duration: 0.8, repeat: Infinity, ease: "easeInOut", delay: 0.15 }}
              />
              <motion.div 
                className="w-2 h-2 bg-green-500 rounded-full" 
                animate={{ y: [0, -4, 0] }}
                transition={{ duration: 0.8, repeat: Infinity, ease: "easeInOut", delay: 0.3 }}
              />
            </div>
            <span className="text-xs text-zinc-400">
              Gemini is thinking...
            </span>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default TypingIndicator;
