import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import ErrorAlert from '../components/ErrorAlert';
import LoadingSpinner from '../components/LoadingSpinner';
import DemoInstructions from '../components/DemoInstructions';
import { useAuth } from '../hooks/useAuth';

const Login: React.FC = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showInstructions, setShowInstructions] = useState(false);
  const { login, isAuthenticated, error, loading } = useAuth();
  const navigate = useNavigate();

  // Show demo instructions on first load
  useEffect(() => {
    setShowInstructions(true);
  }, []);

  // Fill demo credentials when instructions are closed
  const handleCloseInstructions = () => {
    setShowInstructions(false);
    setFormData({
      email: 'demo@example.com',
      password: 'password123',
    });
  };

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/chat');
    }
  }, [isAuthenticated, navigate]);

  useEffect(() => {
    if (error) {
      setErrorMessage(error);
      setIsSubmitting(false);
    }
  }, [error]);

  const { email, password } = formData;

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrorMessage(null);
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setIsSubmitting(true);
    try {
      await login(email, password);
    } catch (error) {
      console.error('Login error:', error);
      setIsSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <div className="flex flex-col items-center justify-center px-6 py-8 mx-auto md:h-screen lg:py-0">
        <div className="w-full bg-white rounded-lg shadow-md md:mt-0 sm:max-w-md xl:p-0">
          <div className="p-6 space-y-4 md:space-y-6 sm:p-8">
            <h1 className="text-xl font-bold leading-tight tracking-tight text-gray-900 md:text-2xl">
              Sign in to your account
            </h1>
            {errorMessage && (
              <ErrorAlert 
                message={errorMessage} 
                onDismiss={() => setErrorMessage(null)} 
              />
            )}
            <form className="space-y-4 md:space-y-6" onSubmit={handleSubmit}>
              <div>
                <label htmlFor="email" className="block mb-2 text-sm font-medium text-gray-900">
                  Email
                </label>
                <input
                  type="email"
                  name="email"
                  id="email"
                  className="input"
                  placeholder="name@example.com"
                  value={email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <label htmlFor="password" className="block mb-2 text-sm font-medium text-gray-900">
                  Password
                </label>
                <input
                  type="password"
                  name="password"
                  id="password"
                  placeholder="••••••••"
                  className="input"
                  value={password}
                  onChange={handleChange}
                  required
                  minLength={6}
                />
              </div>
              <button
                type="submit"
                className="w-full btn btn-primary flex justify-center"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <LoadingSpinner size="sm" className="py-1" />
                ) : (
                  'Sign in'
                )}
              </button>
              <div className="flex items-center">
                <button
                  type="button"
                  onClick={() => setShowInstructions(true)}
                  className="text-primary-600 hover:underline text-sm font-medium"
                >
                  View Demo Instructions
                </button>
                <span className="mx-2 text-gray-500">•</span>
                <Link to="/register" className="text-primary-600 hover:underline text-sm font-medium">
                  Create an account
                </Link>
              </div>
            </form>
          </div>
        </div>
      </div>
      
      {showInstructions && <DemoInstructions onClose={handleCloseInstructions} />}
    </div>
  );
};

export default Login;
