import React from 'react';

interface MessageProps {
  content: string;
  timestamp: Date;
  isUser: boolean;
}

const Message: React.FC<MessageProps> = ({ content, timestamp, isUser }) => {
  const messageTime = new Date(timestamp).toLocaleTimeString([], {
    hour: '2-digit',
    minute: '2-digit'
  });

  return (
    <div className={`flex ${isUser ? 'justify-end' : 'justify-start'} mb-4`}>
      <div
        className={`message ${
          isUser ? 'message-user' : 'message-bot'
        } shadow-message rounded-2xl px-4 py-3 max-w-xs md:max-w-md lg:max-w-lg`}
      >
        <div className="text-sm">{content}</div>
        <div className="text-xs text-gray-500 text-right mt-1">{messageTime}</div>
      </div>
    </div>
  );
};

export default Message;
