import React, { createContext, useState, useEffect, ReactNode, useContext } from 'react';
import { validateGeminiKey } from '../services/geminiService';

export type GeminiModelType = 'gemini-1.5-pro' | 'gemini-1.5-flash';

interface GeminiContextType {
  apiKey: string;
  isValidKey: boolean;
  isLoading: boolean;
  error: string | null;
  selectedModel: GeminiModelType;
  setApiKey: (key: string) => void;
  setSelectedModel: (model: GeminiModelType) => void;
  validateKey: () => Promise<boolean>;
  resetError: () => void;
}

const defaultValue: GeminiContextType = {
  apiKey: '',
  isValidKey: false,
  isLoading: false,
  error: null,
  selectedModel: 'gemini-1.5-pro',
  setApiKey: () => {},
  setSelectedModel: () => {},
  validateKey: async () => false,
  resetError: () => {},
};

export const GeminiContext = createContext<GeminiContextType>(defaultValue);

interface GeminiProviderProps {
  children: ReactNode;
}

export const useGemini = () => {
  const context = useContext(GeminiContext);
  
  if (!context) {
    throw new Error('useGemini must be used within a GeminiProvider');
  }
  
  return context;
};

export const GeminiProvider: React.FC<GeminiProviderProps> = ({ children }) => {
  // Hardcode the API key and consider it valid
  const [apiKey] = useState<string>("AIzaSyAsdXIijrxubtnxPX6pSfXhwZnVc3N6HBw");
  const [isValidKey, setIsValidKey] = useState<boolean>(true);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedModel, setSelectedModel] = useState<GeminiModelType>('gemini-1.5-pro');

  // Keep the validateKey function for API consistency, but simplify it
  const validateKey = async (): Promise<boolean> => {
    return true; // Always return true since we're using a hardcoded key
  };

  // Simple setter to maintain the interface but it won't actually change the key
  const setApiKey = (key: string) => {
    // No-op - we're using the hardcoded key only
    console.log("API key changes are disabled - using hardcoded key");
  };

  const resetError = () => {
    setError(null);
  };

  return (
    <GeminiContext.Provider
      value={{
        apiKey,
        isValidKey,
        isLoading,
        error,
        selectedModel,
        setApiKey,
        setSelectedModel,
        validateKey,
        resetError,
      }}
    >
      {children}
    </GeminiContext.Provider>
  );
};
