import React, { createContext, useContext, useState, useEffect } from 'react';
import { validateGeminiKey } from '../services/geminiService';

interface GeminiContextType {
  apiKey: string;
  setApiKey: (key: string) => void;
  isKeyValid: boolean;
  isLoading: boolean;
  error: string | null;
}

const GeminiContext = createContext<GeminiContextType | undefined>(undefined);

export const GeminiProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [apiKey, setApiKey] = useState<string>('');
  const [isKeyValid, setIsKeyValid] = useState<boolean>(false);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const storedKey = localStorage.getItem('geminiApiKey');
    if (storedKey) {
      setApiKey(storedKey);
      validateApiKey(storedKey);
    }
  }, []);

  const validateApiKey = async (key: string) => {
    setIsLoading(true);
    setError(null);
    
    try {
      const { isValid, errorMessage } = await validateGeminiKey(key);
      setIsKeyValid(isValid);
      if (!isValid && errorMessage) {
        setError(errorMessage);
      }
    } catch (err) {
      setIsKeyValid(false);
      setError('Failed to validate API key');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSetApiKey = (key: string) => {
    setApiKey(key);
    localStorage.setItem('geminiApiKey', key);
    validateApiKey(key);
  };

  return (
    <GeminiContext.Provider
      value={{
        apiKey,
        setApiKey: handleSetApiKey,
        isKeyValid,
        isLoading,
        error
      }}
    >
      {children}
    </GeminiContext.Provider>
  );
};

export const useGemini = () => {
  const context = useContext(GeminiContext);
  if (context === undefined) {
    throw new Error('useGemini must be used within a GeminiProvider');
  }
  return context;
};
