import React, { useRef, useEffect, useState, useCallback } from 'react';
import Header from '../components/Header';
import Message from '../components/Message';
import ChatInput from '../components/ChatInput';
import ErrorAlert from '../components/ErrorAlert';
import LoadingSpinner from '../components/LoadingSpinner';
import TypingIndicator from '../components/TypingIndicator';
import useChat from '../hooks/useChat';
import { SparklesIcon } from '@heroicons/react/24/outline';

const Chat: React.FC = () => {
  const { messages, isLoading, isTyping, error, sendMessage } = useChat();
  const [showError, setShowError] = useState<boolean>(!!error);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const messagesContainerRef = useRef<HTMLDivElement>(null);

  // Scroll to bottom when messages change or when typing animation is happening
  const scrollToBottom = useCallback(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, isTyping, scrollToBottom]);

  // Show error when error state changes
  useEffect(() => {
    setShowError(!!error);
  }, [error]);

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-gray-50 to-gray-100 dark:from-dark-900 dark:to-dark-800 transition-colors duration-200">
      {/* Fixed Header */}
      <div className="fixed top-0 left-0 right-0 z-50">
        <Header />
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col pt-16 max-w-6xl w-full mx-auto px-4 md:px-6 pb-4">
        <div className="bg-white dark:bg-dark-800 rounded-2xl shadow-xl flex flex-col h-[calc(100vh-8rem)]">
          {/* Chat header */}
          <div className="p-4 md:p-6 bg-gradient-to-r from-primary-500 to-primary-600 text-white flex items-center justify-between sticky top-0 z-10">
            <h1 className="text-xl font-bold flex items-center gap-2">
              <SparklesIcon className="h-6 w-6 text-white animate-pulse" />
              My Chatbot
            </h1>

            <div className="flex items-center gap-3">
              <span className="text-xs px-3 py-1.5 rounded-full bg-green-500/30 text-white font-medium">
                Gemini Enabled
              </span>
            </div>
          </div>

          {/* Chat messages */}
          <div 
            ref={messagesContainerRef}
            className="flex-1 overflow-y-auto p-4 md:p-6 bg-gray-50 dark:bg-dark-900/50 backdrop-blur-sm"
          >
            <div className="max-w-4xl mx-auto w-full space-y-4">
              {showError && error && (
                <div className="mb-4">
                  <ErrorAlert 
                    message={error} 
                    onDismiss={() => setShowError(false)} 
                  />
                </div>
              )}

              {messages.length === 0 ? (
                <div className="h-full flex flex-col items-center justify-center text-center p-6">
                  <div className="max-w-md space-y-6 bg-white dark:bg-dark-800 rounded-2xl p-8 shadow-lg">
                    <div className="h-20 w-20 bg-gradient-to-br from-primary-400 to-primary-600 rounded-full flex items-center justify-center mx-auto mb-2">
                      <SparklesIcon className="h-10 w-10 text-white" />
                    </div>
                    <h2 className="text-2xl font-bold text-gray-800 dark:text-white">Welcome to Gemini Chat</h2>
                    <p className="text-gray-600 dark:text-gray-300">
                      This AI assistant is powered by Google's Gemini AI model. Ask anything and get intelligent responses instantly.
                    </p>
                    <div className="pt-4 border-t border-gray-200 dark:border-dark-700">
                      <p className="text-gray-500 dark:text-gray-400 text-sm">
                        Start a conversation by typing a message below.
                      </p>
                    </div>
                  </div>
                </div>
              ) : (
                <>
                  {isLoading && messages.length === 0 ? (
                    <div className="h-full flex flex-col items-center justify-center py-12">
                      <LoadingSpinner />
                      <p className="mt-4 text-gray-500 dark:text-gray-400">Loading conversation...</p>
                    </div>
                  ) : (
                    <>
                      {messages.map((message) => (
                        <Message
                          key={message.id}
                          content={message.content}
                          timestamp={message.timestamp}
                          isUser={message.isUser}
                        />
                      ))}
                      {isTyping && <TypingIndicator />}
                      <div ref={messagesEndRef} className="h-4" />
                    </>
                  )}
                </>
              )}
            </div>
          </div>

          {/* Fixed Chat Input */}
          <div className="sticky bottom-0 bg-white dark:bg-dark-800 border-t border-gray-200 dark:border-dark-700 p-4">
            <div className="max-w-4xl mx-auto w-full">
              <ChatInput 
                onSendMessage={sendMessage} 
                disabled={isLoading || isTyping} 
              />
              <p className="text-xs text-center text-gray-500 dark:text-gray-400 mt-2">
                Gemini may display inaccurate info, including about people, so double-check its responses.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
